function scrollToTop(){const e=document.documentElement;e.scrollTo({top:0}),e.focus()}const scrollToTopButton=document.getElementById("scroll-to-top");function showSubMenu(){this.querySelector(".axel-submenu").classList.add("axel-submenu--show")}function hideSubMenu(){this.querySelector(".axel-submenu").classList.remove("axel-submenu--show")}scrollToTopButton&&scrollToTopButton.addEventListener("click",scrollToTop);const hasChildren=document.getElementsByClassName("axel-menu__has-children");hasChildren&&Array.from(hasChildren).forEach(e=>{e.addEventListener("mouseover",showSubMenu),e.addEventListener("mouseout",hideSubMenu)});